import React, { Component } from "react";
import "./style.css";

import { useDrag } from "react-dnd";

const CardBig = ({ id = "123" }) => {
  const [{ isRowDragging }, dragRef] = useDrag({
    type: "CARD",
    item: {
      id: id,
    },
    collect: (monitor) => ({
      isRowDragging: monitor.isDragging(),
      opacity: monitor.isDragging(),
    }),
  });

  //   console.log("dragging", isRowDragging);
  return (
    <div ref={dragRef} className="main-card">
      hELLO
    </div>
  );
};

export default CardBig;
